	final String clueText;
	final int clueID;
	final ClueTier clueTier;

	@Getter
	final OrRequirement regions;

	Clues(String text, int clueID, ClueTier clueTier, List<WorldPoint> wps)
	{
		this.clueText = text;
		this.clueID = clueID;
		this.clueTier = clueTier;
		this.regions = new OrRequirement(wps);
	}

	public static Clues get(int id)
	{
		for (Clues clue : Clues.values())
		{
			if (clue.getClueID() == id)
			{
				return clue;
			}
		}
		return null;
	}
